# PowerShell Job
#----------------------------------------------------------------------------------------------------------------------------------#
function PowerShell_Job_Persist {
Write-host -ForegroundColor Yellow "Set PowerShell Job..."
Start-Sleep 2
$trigger = New-JobTrigger -Once -At (Get-Date).AddMinutes(1) -RepetitionInterval (New-TimeSpan -Minutes 5) -RepetitionDuration ([TimeSpan]::MaxValue)
$options = New-ScheduledJobOption –HideInTaskScheduler
Register-ScheduledJob –Name "ShockSOC" -ScriptBlock {calc.exe} –Trigger $trigger –ScheduledJobOption $options
Write-host -ForegroundColor Green "Done!"
}
